import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { 
  Container, 
  Typography, 
  Box, 
  Paper, 
  Button, 
  Grid,
  Card,
  CardMedia,
  CardContent,
  Divider,
  Alert,
  IconButton,
  Tooltip
} from '@mui/material';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import StopIcon from '@mui/icons-material/Stop';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import RefreshIcon from '@mui/icons-material/Refresh';

const VisualizerPage = ({ visualizations, status }) => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [visualization, setVisualization] = useState(null);
  const [isRunning, setIsRunning] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  // Récupérer les informations de la visualisation à partir de l'ID
  useEffect(() => {
    const viz = visualizations.find(v => v.id === id);
    if (viz) {
      setVisualization(viz);
    } else if (visualizations.length > 0) {
      // Si l'ID n'est pas trouvé mais que nous avons des visualisations chargées
      setErrorMessage('Visualisation non trouvée');
    }
  }, [id, visualizations]);

  // Mettre à jour le statut d'exécution
  useEffect(() => {
    if (id in status) {
      setIsRunning(status[id] === 'running');
    }
  }, [id, status]);

  // Fonction pour démarrer une visualisation
  const handleStart = async () => {
    try {
      setErrorMessage('');
      await axios.post(`/api/start/${id}`);
      // Le statut sera mis à jour via le polling dans App.js
    } catch (error) {
      console.error(`Erreur lors du démarrage de la visualisation:`, error);
      setErrorMessage(error.response?.data?.message || error.message);
    }
  };

  // Fonction pour arrêter une visualisation
  const handleStop = async () => {
    try {
      setErrorMessage('');
      await axios.post(`/api/stop/${id}`);
      // Le statut sera mis à jour via le polling dans App.js
    } catch (error) {
      console.error(`Erreur lors de l'arrêt de la visualisation:`, error);
      setErrorMessage(error.response?.data?.message || error.message);
    }
  };

  // Fonction pour rafraîchir le statut manuellement
  const refreshStatus = async () => {
    try {
      const response = await axios.get('/api/status');
      if (id in response.data) {
        setIsRunning(response.data[id] === 'running');
      }
    } catch (error) {
      console.error('Erreur lors de la récupération du statut:', error);
    }
  };

  // Générer une image de prévisualisation (pour démonstration)
  const getImageUrl = (id) => {
    return `https://picsum.photos/seed/${id}/800/400`;
  };

  // Si la visualisation n'est pas trouvée
  if (!visualization && !errorMessage && visualizations.length > 0) {
    return (
      <Container maxWidth="lg">
        <Box sx={{ my: 4, textAlign: 'center' }}>
          <Typography variant="h4">Visualisation non trouvée</Typography>
          <Button 
            startIcon={<ArrowBackIcon />} 
            variant="contained" 
            sx={{ mt: 2 }} 
            onClick={() => navigate('/')}
          >
            Retour au Tableau de Bord
          </Button>
        </Box>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg">
      {/* Bouton Retour */}
      <Button 
        startIcon={<ArrowBackIcon />} 
        sx={{ mb: 2 }} 
        onClick={() => navigate('/')}
      >
        Retour au Tableau de Bord
      </Button>

      {errorMessage && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {errorMessage}
        </Alert>
      )}

      {visualization && (
        <>
          <Paper sx={{ p: 3, mb: 3 }}>
            <Grid container spacing={3}>
              <Grid item xs={12} md={8}>
                <Typography variant="h4" gutterBottom>
                  {visualization.name}
                </Typography>

                <Box sx={{ mb: 2, display: 'flex', alignItems: 'center' }}>
                  <Typography 
                    variant="subtitle1"
                    sx={{ 
                      color: isRunning ? 'success.main' : 'error.main',
                      fontWeight: 'bold',
                      mr: 1
                    }}
                  >
                    {isRunning ? 'En cours d\'exécution' : 'Arrêté'}
                  </Typography>
                  <Tooltip title="Rafraîchir le statut">
                    <IconButton size="small" onClick={refreshStatus}>
                      <RefreshIcon fontSize="small" />
                    </IconButton>
                  </Tooltip>
                </Box>

                <Typography variant="body1" paragraph>
                  {visualization.description || "Aucune description détaillée disponible pour cette visualisation."}
                </Typography>

                <Box sx={{ mt: 3 }}>
                  {!isRunning ? (
                    <Button 
                      variant="contained" 
                      color="primary" 
                      startIcon={<PlayArrowIcon />}
                      onClick={handleStart}
                    >
                      Démarrer la Visualisation
                    </Button>
                  ) : (
                    <Button 
                      variant="contained" 
                      color="error" 
                      startIcon={<StopIcon />}
                      onClick={handleStop}
                    >
                      Arrêter la Visualisation
                    </Button>
                  )}
                </Box>
              </Grid>

              <Grid item xs={12} md={4}>
                <Card>
                  <CardMedia
                    component="img"
                    height="200"
                    image={getImageUrl(visualization.id)}
                    alt={visualization.name}
                  />
                  <CardContent>
                    <Typography variant="subtitle2" color="text.secondary">
                      Module: {visualization.module}
                    </Typography>
                    <Typography variant="subtitle2" color="text.secondary">
                      Classe: {visualization.class}
                    </Typography>
                  </CardContent>
                </Card>
              </Grid>
            </Grid>
          </Paper>

          <Paper sx={{ p: 3 }}>
            <Typography variant="h5" gutterBottom>
              Informations techniques
            </Typography>
            <Divider sx={{ mb: 2 }} />
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <Typography variant="subtitle2" color="text.secondary">
                  Identifiant:
                </Typography>
                <Typography variant="body2" gutterBottom>
                  {visualization.id}
                </Typography>
              </Grid>
              <Grid item xs={12} sm={6}>
                <Typography variant="subtitle2" color="text.secondary">
                  Chemin du module:
                </Typography>
                <Typography variant="body2" gutterBottom>
                  {visualization.module}
                </Typography>
              </Grid>
              <Grid item xs={12}>
                <Typography variant="subtitle2" color="text.secondary">
                  Notes:
                </Typography>
                <Typography variant="body2">
                  Pour modifier les paramètres de la visualisation, utilisez les touches fléchées lorsque la fenêtre de visualisation est active.
                </Typography>
              </Grid>
            </Grid>
          </Paper>
        </>
      )}
    </Container>
  );
};

export default VisualizerPage;
